### Day 1-1 ###
input = ""
sum = 0
for c in range(0, len(input)):
    if input[c] == input[(c+1) % len(input)]:
        sum += int(input[c])
print("Day 1-1: "+str(sum))

### Day 1-2 ###
sum = 0
half = int(len(input)/2)
for c in range(0, len(input)-1):
    if input[c] == input[(c+half) % len(input)]:
        sum += int(input[c])
print("Day 1-2: "+str(sum))

